import styled from "styled-components";

export const HeroContainer = styled.div`
  width: 100%;
  height: 50vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const HeroWrapper = styled.div`
  width: 100%;
  max-width: 800px;
  padding: 0 24px;
  }
`;

export const HeroH1 = styled.h1`
  text-align: center;
  font-size: 2rem;
