import React from "react";
import "./sectionSix.css";
import ReactSimpleCarousel from "react-spring-carousel";
import Image1 from "./images/test.png";
import Image2 from "./images/test.png";
import Image3 from "./images/test.png";

const slides = [
  {
    image: Image1,
  },
  {
    image: Image2,
  },
  {
    image: Image3,
  },
];
function Carousel() {
  return (
    <div className="App">
      <ReactSimpleCarousel slidesToShow={1}>
        {slides.map(({ image, text }, i) => (
          <div key={i} className="App-slide">
            <a href={`#${i}`} draggable="false">
              <img src={image} alt="" />
            </a>
            <p
              style={{
                height: "100%",
                // position: "absolute",
                // top: "50%",
                // left: "50%",
                transform: "translateX(-50%) translateY(-50%)",
              }}
            >
              {text}
            </p>
          </div>
        ))}
      </ReactSimpleCarousel>
    </div>
  );
}
export default Carousel;
